from CAS_USER
where 1 = 1

	and USER_ID=#{userId}


	and USER_GROUP_ID=#{userGroupId}


	and USER_LV_ID=#{userLvId}


	and USERNAME=#{username}


	and PASSWORD=#{password}


	and FREEZE=#{freeze}


	and ISTHERE=#{isthere}


	and THIRDID=#{thirdid}


	and SALT=#{salt}


	and ENCRYPT=#{encrypt}


	and TEL=#{tel}


	and PHONE=#{phone}


	and VERIFIEDPHONE=#{verifiedphone}


	and EMAIL=#{email}


	and VERIFIEDEMAIL=#{verifiedemail}


	and NICKNAME=#{nickname}


	and REALNAME=#{realname}


	and IDCARD=#{idcard}


	and GENDER=#{gender}


