	and DEVICE_TYPE=#{deviceType}


	and DEVICE_NO=#{deviceNo}


	and CHANNELID=#{channelid}


	and STATUS=#{status}


	and IS_VERIFY=#{isVerify}


	and IS_DEL=#{isDel}


	and LAST_LOGIN_TIME=#{lastLoginTime}


	and LAST_LOGIN_IP=#{lastLoginIp}


	and BAN_ETIME=#{banEtime}


	and CTIME=#{ctime}


	and MTIME=#{mtime}


	and TELPHONE=#{telphone}


