mapper.select:
	select 
	ID,
	PARENT_ID,
	PARENT_IDS,
	NAME,
	SORT,
	HREF,
	TARGET,
	ICON,
	IS_SHOW,
	PERMISSION,
	CREATE_BY,
	CREATE_DATE,
	UPDATE_BY,
	UPDATE_DATE,
	REMARKS,
	DEL_FLAG
	from SYS_MENU


        ID=#{id}


       and PARENT_ID=#{parentId}


       and PARENT_IDS=#{parentIds}


       and NAME=#{name}


       and SORT=#{sort}


       and HREF=#{href}


       and TARGET=#{target}


       and ICON=#{icon}


       and IS_SHOW=#{isShow}


       and PERMISSION=#{permission}


       and CREATE_BY=#{createBy}


       and CREATE_DATE=#{createDate}


       and UPDATE_BY=#{updateBy}


       and UPDATE_DATE=#{updateDate}


       and REMARKS=#{remarks}


       and DEL_FLAG=#{delFlag}

