mapper.select:
select 
  property_id,
  name,
  val_inputtype,
  label_name,
  placeholder,
  note,
  sort_order,
  is_spec,
  status
from trend_property


     property_id=#{propertyId}


     and name=#{name}


     and val_inputtype=#{valInputtype}


     and label_name=#{labelName}


     and placeholder=#{placeholder}


     and note=#{note}


     and sort_order=#{sortOrder}


     and is_spec=#{isSpec}


     and status=#{status}

select 
  property_id,
  name,
  val_inputtype,
  label_name,
  placeholder,
  note,
  sort_order,
  is_spec,
  status
from trend_property


     property_id=#{propertyId}


     and name=#{name}


     and val_inputtype=#{valInputtype}


     and label_name=#{labelName}


     and placeholder=#{placeholder}


     and note=#{note}


     and sort_order=#{sortOrder}


     and is_spec=#{isSpec}


     and status=#{status}

mapper.sql:
	   from trend_property p,trend_property_value v

            p.property_id = v.property_id

            and p.property_id=#{propertyId}


            and p.name=#{name}


            and p.val_inputtype=#{valInputtype}


            and p.label_name=#{labelName}


            and p.placeholder=#{placeholder}


            and p.note=#{note}


            and p.sort_order=#{sortOrder}


            and p.is_spec=#{isSpec}


            and p.status=#{status}


       group by 
                v.property_id
